import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Grid from "@material-ui/core/Grid";
import ImageCard from './ImageCard';
import Dialog from '@material-ui/core/Dialog';
import FlatButton from 'material-ui/FlatButton';
import './ImageResult.css'


class ImageResults extends Component {

    state= {
        open:false,
        currentImage:''
    }

    handleOpen = img =>{
        this.setState({open:true, currentImage:img})
    }

    handleClose = img =>{
        this.setState({open:false})
    }

    render() {
        
        const {images}=this.props
        
        let imageListContent;
        if(this.props.loading){
            imageListContent = (
                <Spinner />
            )
        }

        if(images){
            imageListContent = (
                <div>
                <Grid container className='results' spacing={3}>
                    {images.map(img=>(
                        <div className='mycard'>
                            <ImageCard onClick={()=>this.handleOpen(img.largeImageURL)}
                            clicked={()=>this.handleOpen(img.largeImageURL)}
                            tags={img.tags}
                            key={img.id}
                            user={img.user}
                            src={img.largeImageURL}
                            avatar={img.userImageURL}
                            favorites={img.favorites}
                            downloads={img.downloads}
                            views={img.views}
                        />
                        </div>
                       
                    ))}
                </Grid>
            </div>
                
            )
        }

        const actions =[
            <FlatButton label="Close" primary={true} onClick={this.handleClose} />
        ];

        return (
            <div>
                {imageListContent}
                <Dialog
                    actions={actions}
                    modal={false}
                    open={this.state.open}
                    onRequestClose={this.handleClose}>
                     <img src={this.state.currentImage} alt='' style={{'width':'100%'}}/>
                 </Dialog>
            </div>
        )
    }
}

ImageResults.propTypes = {
    images: PropTypes.array.isRequired
}

export default ImageResults;
