import React, { Component } from 'react';
import TextField from 'material-ui/TextField'
import SelectField from 'material-ui/SelectField';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import axios from 'axios';
import Select from '@material-ui/core/Select';
import ImageResults from '../image_results/ImageResults';

export default class Search extends Component {

    state = {
        searchText:'',
        amount:50,
        apiUrl:'https://pixabay.com/api',
        apiKey: '7129816-eca694d6cc4ede47255ad861c',
        images: []
    }

    onTextChange = (e)=>{
        const val = e.target.value
        this.setState(()=>({searchText: val}),()=>{
            if(val===''){
                this.setState((state)=>({images:[]}))
            }else {
                axios.get(`${this.state.apiUrl}/?key=${this.state.apiKey}&q=${this.state.searchText}&image_type=photo&per_page=${this.state.amount}&safesearch=true`)
                .then(res=>this.setState({images: res.data.hits}))
                .catch(err=>{
                    console.log(err)
                })
            }
        })
        console.log(this.state.searchText)
        console.log('images from searchjs',this.state.images)
    }

    onAmountChange = (event)=>{
        this.setState({amount: event.target.value})
    }

    render() {
        return (
            <div>
                <TextField 
                    name="searchText"
                    value={this.setState.searchText}
                    onChange={this.onTextChange}
                    floatingLabelText="Search for images"
                    fullWidth={true}
                />
                <FormControl style={{width:'200px', margin:'10px auto'}}>
                    <InputLabel id="demo-simple-select-label">Amount</InputLabel>
                    <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={this.state.amount}
                    onChange={this.onAmountChange}
                    >
                    <MenuItem value={5}>5</MenuItem>
                    <MenuItem value={10}>10</MenuItem>
                    <MenuItem value={15}>15</MenuItem>
                    <MenuItem value={30}>30</MenuItem>
                    <MenuItem value={50}>50</MenuItem>
                    </Select>
                </FormControl>
                <br />
                {this.state.images.length > 0 ? <ImageResults images={this.state.images} searchText={this.state.searchText}/>:null}

            </div>
        )
    }
}
